import React, { useState } from "react";

function App() {
  const [isLogin, setIsLogin] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [message, setMessage] = useState("");

  const handleSignup = async (e) => {
    e.preventDefault();
    setMessage("");
    const res = await fetch("/api/signup", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ name, email, password }),
    });
    const data = await res.json();
    setMessage(data.message || "Signup attempted");
    if (res.ok) {
      setIsLogin(true);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setMessage("");
    const res = await fetch("/api/login", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ email, password }),
    });
    const data = await res.json();
    setMessage(data.message || "Login attempted");
  };

  return (
    <div style={{ maxWidth: 400, margin: "auto", padding: 20 }}>
      {isLogin ? (
        <>
          <h2>Login</h2>
          <form onSubmit={handleLogin}>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
              style={{ width: "100%", marginBottom: 10 }}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
              style={{ width: "100%", marginBottom: 10 }}
            />
            <button type="submit" style={{ width: "100%" }}>Login</button>
          </form>
          <p style={{ marginTop: 10 }}>
            Don't have an account?{' '}
            <button type="button" onClick={() => { setIsLogin(false); setMessage(""); }} style={{ background: 'none', border: 'none', color: 'blue', textDecoration: 'underline', cursor: 'pointer', padding: 0 }}>Sign up</button>
          </p>
        </>
      ) : (
        <>
          <h2>Sign Up</h2>
          <form onSubmit={handleSignup}>
            <input
              type="text"
              placeholder="Name"
              value={name}
              onChange={e => setName(e.target.value)}
              required
              style={{ width: "100%", marginBottom: 10 }}
            />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
              style={{ width: "100%", marginBottom: 10 }}
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
              style={{ width: "100%", marginBottom: 10 }}
            />
            <button type="submit" style={{ width: "100%" }}>Sign Up</button>
          </form>
          <p style={{ marginTop: 10 }}>
            Already have an account?{' '}
            <button type="button" onClick={() => { setIsLogin(true); setMessage(""); }} style={{ background: 'none', border: 'none', color: 'blue', textDecoration: 'underline', cursor: 'pointer', padding: 0 }}>Login</button>
          </p>
        </>
      )}
      {message && <p style={{ marginTop: 10 }}>{message}</p>}
    </div>
  );
}

export default App;
